    private void addboxActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addboxActionPerformed
        
        BasicInfoForm info = new BasicInfoForm();
        ColourForm colour = new ColourForm();
        
        info.setVisible(true);
        Object result = await(info);
        info.close();
        
        
    }//GEN-LAST:event_addboxActionPerformed

    private Object await(Form form) {
        Object result = null;
        while (result == null) {
            result = form.getInfo();
        }
        return result;
    }
    
